import { Component, OnInit, ElementRef, Renderer2, AfterViewChecked, AfterViewInit } from '@angular/core';
import { LoginService } from '../shared/services/login.service';
import { Router } from '@angular/router';
import { HighlightService } from '../shared/services/highlight.service';
import { FormBuilder } from '@angular/forms';

@Component({
  selector: 'app-landing',
  templateUrl: './landing.component.html',
  styleUrls: ['./landing.component.css']
})
export class LandingComponent implements OnInit, AfterViewChecked, AfterViewInit,  {
  
  htmlContent: string = '';
  language: string = '';

  //sub!: Subscription;
  highlighted: boolean = false;
  codeType = 'typescript'

  teste = `constructor(
    private router: Router,
    private loginService: LoginService,
    private highlightService: HighlightService,
    private fb: FormBuilder,
    private renderer: Renderer2,
    private el: ElementRef
  ){}`;

  form = this.fb.group({
    content: ''
  });

  get contentControl() {
    return this.form.get('content');
  }

  constructor(
    private router: Router,
    private loginService: LoginService,
    private highlightService: HighlightService,
    private fb: FormBuilder,
    private renderer: Renderer2,
    private el: ElementRef
  ){}
  
  ngOnInit(): void {
    if(this.loginService.isLoggedIn()){
      this.router.navigate(['/home']);
    }
  }
  
  ngAfterViewChecked(){
    this.highlightService.highlightAll();
  }

  updateLanguage(){
    const codeArea = this.el.nativeElement.querySelector('#codeArea');
    this.renderer.removeClass(codeArea, this.language);
    this.renderer.addClass(codeArea, this.language);
    this.highlightService.highlightAll();
  }
}
