import { Component, ElementRef, HostListener, OnInit } from '@angular/core';
import { LoginService } from '../../services/login.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-navbar-logged-in',
  templateUrl: './navbar-logged-in.component.html',
  styleUrls: ['./navbar-logged-in.component.css']
})
export class NavbarLoggedInComponent {

  constructor(
    private elementRef: ElementRef,
    private loginService: LoginService,
    private router: Router
  ){}

  logout(){
    this.loginService.logout();
    this.router.navigate(['/']);
  }

  // Dropdown
  isDropdownOpen: boolean = false;
  isDropdownAnimated: boolean = false;
  toggleDropdown() {
    this.isDropdownOpen = !this.isDropdownOpen;
    this.isDropdownAnimated = true;
  }

  closeDropdown() {
    this.isDropdownOpen = false;
    this.isDropdownAnimated = false;
  }

  @HostListener('document:click', ['$event'])
  onDocumentClick(event: MouseEvent) {
    // Verifica se o clique ocorreu dentro do dropdown
    if (!this.elementRef.nativeElement.contains(event.target)) {
      this.isDropdownOpen = false;
      this.isDropdownAnimated = false;
    }
  }
}
<nav class="navbar navbar-expand-lg bg-body-tertiary">
  <div class="container-fluid">
    <a class="navbar-brand"></a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent1"
        aria-controls="navbarSupportedContent1" aria-expanded="false" aria-label="Toggle navigation">
        <span class="navbar-toggler-icon"></span>
    </button>
    <div class="collapse navbar-collapse" id="navbarSupportedContent1">

      <div class="search-bar">
        <div class="input-group">
          <input type="search" class="form-control" placeholder="Busca" aria-label="Busca"
            aria-describedby="button-addon2">
          <button class="btn btn-primary btn-icon btn-model" type="button"><span><i
                class="fas fa-search"></i></span></button>
        </div>
      </div>

      <div>
        <a class="nav-link dropdown-toggle" id="userDropdown" role="button" (click)="toggleDropdown()">
          <span class="mr-2 d-none d-lg-inline text-gray-600 small">Lucas Teixeira</span>
          <img class="img-profile rounded-circle profile-pic" src="../../../assets/img/profile-pics/me.jpg">
        </a>

        <div id="userDropdownMenu" class="perfil-dropdown dropdown-menu dropdown-menu-right shadow animated"
        [ngClass]="{
          'show': isDropdownOpen,
          'fadeIn': isDropdownAnimated,
          'fadeOut': !isDropdownOpen && !isDropdownAnimated
        }" aria-labelledby="userDropdown" (click)="closeDropdown()"
        >
          <a class="dropdown-item" [routerLink]="['/profile']">
            <i class="fas fa-user fa-sm fa-fw mr-2 text-gray-400"></i>
            Perfil
          </a>
          <a class="dropdown-item" [routerLink]="['/settings']">
            <i class="fas fa-cogs fa-sm fa-fw mr-2 text-gray-400"></i>
            Configurações
          </a>
          <a class="dropdown-item" [routerLink]="['/activity-log']">
            <i class="fas fa-list fa-sm fa-fw mr-2 text-gray-400"></i>
            Atividades
          </a>
          <div class="dropdown-divider"></div>
          <a class="dropdown-item logout" (click)="logout()" data-toggle="modal" data-target="#logoutModal">
            <i class="fas fa-sign-out-alt fa-sm fa-fw mr-2 text-gray-400"></i>
            Sair
          </a>
        </div>
      </div>
    </div>
  </div>
</nav>
