import { Component } from '@angular/core';

@Component({
  selector: 'app-sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.css']
})
export class SidebarComponent {
  isMenuToggled = false;
  isMenuToggled2 = false;

  ngOnInit() {
    this.initMenu();
  }

  toggleMenu() {
    this.isMenuToggled = !this.isMenuToggled;
  }

  toggleMenu2() {
    this.isMenuToggled2 = !this.isMenuToggled2;
    this.hideMenuUl();
  }

  initMenu() {
    this.hideMenuUl();
    const currentElement = document.querySelector('#menu ul .current');
    if (currentElement) {
      const parentElement = currentElement.parentElement as HTMLElement;
      if (parentElement) {
        parentElement.style.display = 'block';
      }
    }
  }

  hideMenuUl() {
    const menuUlElements = document.querySelectorAll('#menu ul');
    menuUlElements.forEach(element => {
      (element as HTMLElement).style.display = 'none';
    });
  }

  handleClick(event: Event) {
    const target = event.target as HTMLElement;
    const checkElement = target.nextElementSibling;
    if (checkElement && checkElement.tagName === 'UL') {
      if ((checkElement as HTMLElement).style.display === 'block') {
        return false;
      } else {
        this.hideMenuUl();
        (checkElement as HTMLElement).style.display = 'block';
        return false;
      }
    }
